# install docker
Manually install docker on clusterapi nodes with the install-docker.sh
curl -fsSL https://get.docker.com -o get-docker.sh
chmod +x get-docker.sh

kubectl create serviceaccount avi -n default

# create clusterrolebinding.yaml + clusterrole


# get secret -token
kubectl describe serviceaccount avi -n default

kubectl describe secret avi-token-c2hm9 -n default


